    private void JButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JButtonActionPerformed
        try(Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3307/rentcar", "root", "Duoc.2023")){
            //System.out.println("Conectado!.");
            String sql = "insert into vehiculo values (?, ?, ?, ?)";
            try(PreparedStatement ps = con.prepareStatement(sql)){
                ps.setString(1, jtfPatente.getText());
                ps.setString(2, jtfMarca.getText());
                ps.setString(3, jtfPrecio.getText());
                ps.setBoolean(4, jchbNuevo.isSelected());
                ps.executeUpdate();
                JOptionPane.showMessageDialog(this, "Datos Insertados!.");
            }
        }catch(SQLException sqle){
            System.out.println("Error al conectar: " + sqle);
        }
    }//GEN-LAST:event_JButtonActionPerformed

