    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try(Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3307/rentcar", "root", "Duoc.2023")){
            //System.out.println("Conectado!.");
            String sql = "update vehiculo set marca=?, precio=?, nuevo=? where patente=? ";
            try(PreparedStatement ps = con.prepareStatement(sql)){
                ps.setString(4, jtfPatente.getText());
                ps.setString(1, jtfMarca.getText());
                ps.setString(2, jtfPrecio.getText());
                ps.setBoolean(3, jchbNuevo.isSelected());
                ps.executeUpdate();
                JOptionPane.showMessageDialog(this, "Datos Actualizados!.");
            }
        }catch(SQLException sqle){
            System.out.println("Error al conectar: " + sqle);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
            try(Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3307/rentcar", "root", "Duoc.2023")){
            //System.out.println("Conectado!.");
            String sql = "delete from vehiculo where patente=?";
            try(PreparedStatement ps = con.prepareStatement(sql)){
                ps.setString(1, jtfPatente.getText());
                if(JOptionPane.showConfirmDialog(this, "Seguro de eliminar?") ==0){
                   ps.executeUpdate();
                JOptionPane.showMessageDialog(this, "Datos Eliminados!."); 
                }
                
            }
        }catch(SQLException sqle){
            System.out.println("Error al conectar: " + sqle);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

