        public final void listarDatos(){
            jtaDatos.setText("");
             try(Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3307/rentcar", "root", "Duoc.2023")){
            //System.out.println("Conectado!.");
            String sql = "select * from vehiculo";
            try(PreparedStatement ps = con.prepareStatement(sql)){
               try(ResultSet rs = ps.executeQuery()){
                   while(rs.next()){
                       jtaDatos.setText(jtaDatos.getText()
                               +rs.getString("patente")+ " "
                               +rs.getString("marca")+" "
                               +rs.getString("precio")+" " 
                               +rs.getString("nuevo")+"\n "
                       );
                   }
               }
            }
        }catch(SQLException sqle){
            System.out.println("Error al conectar: " + sqle);
        }
        }
    
    
